<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Login extends MY_Controller{public function  __construct(){    parent::__construct();} function  index(){         if($this->session->has_userdata('is_logged_in')==1){             redirect('dashboard');     }     $data['title']='تسجيل الدخول الى نظام ادراة  جمعية رفاق';     $data['metakeyword']='جمعية رفاق';     $data['metadiscription']='جمعية رفاق';    $this->load->view('admin/users/login',$data); }    public  function check_login(){        $this->load->model('Users');        $userdata=$this->Users->check_user_data();        if($userdata !=''){            $userdata['is_logged_in']=true;            $this->session->set_userdata($userdata);            redirect('Dash/home');        }else{            $data['title']='تسجيل الدخول الى نظام ادراة  جمعية رفاق';            $data['metakeyword']='جمعية رفاق';            $data['metadiscription']='جمعية رفاق';            $data['response']="خطأفى بيانات الادخال";            $this->load->view('admin/users/login',$data);        }    }    public function logout(){          $this->load->model('Users');         $this->Users->update_last_login($_SESSION['user_id']);         $this->session->sess_destroy();        redirect('/login','refresh');    }}