 => '#c07852', 3 => '#75bf67', 4 => '#b6ab2d', 5 => '#09b6bb', 6 => '#3088d8', 7 => '#4d92a7', 8 => '#ef6c02', 9 => '#a69fb9', 10 => '#67351b', 11 => '#b6ea47', 12 => '#e18091', 13 => '#f5f44d', 14 => '#a63daa', 15 => '#fb1f73', 16 => '#9b9a92', 17 => '#8f0e0b', 18 => '#397631', 19 => '#074183', 20 => '#cab11e');                            foreach ($tree as $record) {                                $onclick = "$('#account_num').val(" . $record->code . "); $('#account').val('" . $record->name . "'); $('#myModal').modal('hide');";                                if ($record->hesab_no3 == 1) {                                    $onclick =  "swal({ title: 'هذا حساب رئيسي .  ', text:'', type: 'warning', confirmButtonColor: '#3085d6', confirmButtonText: 'تم' }); ";                                } ?>                            <tr style="background-color: <?= $colorArray[$record->level] ?>; cursor: pointer;"                                onclick="<?= $onclick ?>">                                <td class="forTd"><?= $count++ ?></td>                                <td class="forTd"><?= $record->code ?></td>                                <td class="forTd"><?= $record->name ?></td>                                </tr><?php if (isset($record->subs)) {                                    $count = buildTreeTable($record->subs, $count++);                                }                            }                            return $count;                        } ?></tbody>                    </table><?php // } ?></div>                <div class="modal-footer">                    <button type="button" class="btn btn-danger" data-dismiss="modal">إغلاق</button>                </div>            </div>        </div>    </div>    <!--  -->    <script>        function GetDiv_emps(div) {            html = '<div class="col-md-12 no-padding"><table id="js_table_members2" class="table table-striped table-bordered dt-responsive nowrap " >' +                '<thead><tr class="info"><th style="width: 50px;">م</th><th style="width: 50px;"> كود الأدارة  </th><th style="width: 50px;"> أسم الأدارة  </th>' +                '</tr></thead><table><div id="dataMember"></div></div>';            $("#" + div).html(html);            $('#js_table_members2').show();            var oTable_usergroup = $('#js_table_members2').DataTable({                dom: 'Bfrtip',                "ajax": '<?php echo base_url(); ?>human_resources/egraat_settings/Mokfat_setting/getConnection_emp/',                aoColumns: [                    {"bSortable": true},                    {"bSortable": true},                    {"bSortable": true},                ],                buttons: [                    'pageLength',                    'copy',                    'excelHtml5',                    {                        extend: "pdfHtml5",                        orientation: 'landscape'                    },                    {                        extend: 'print',                        exportOptions: {columns: ':visible'},                        orientation: 'landscape'                    },                    'colvis'                ],                colReorder: true,                destroy: true,                "order": [[1, "asc"]]            });        }        //8-6-om        function Get_emp_Name(obj) {            var title_id = obj.dataset.title_id;            var title = obj.dataset.name;            var code = obj.dataset.code;            document.getElementById('title_id').value = title_id;            document.getElementById('title').value = title;            document.getElementById('title_code').value = code;            $("#myModal_emps .close").click();        }    </script>    <!--  -->    <script>        function GetDiv_sub(div) {            html = '<div class="col-md-12 no-padding"><table id="js_table_members2" class="table table-striped table-bordered dt-responsive nowrap " >' +           