alue;            document.getElementById('emp_code').value = emp_code;            $('#emp_id_fk').val(obj.value);            check_ezn(obj.value);            get_emp_sidebar();            check_setting_time($('#emp_id_fk').val());            check_setting($('#emp_id_fk').val());            get_available();            document.getElementById('edara_n').value = edara_n;            document.getElementById('edara_id_fk').value = edara_id;            document.getElementById('job_title').value = job_title;            document.getElementById('qsm_n').value = qsm_n;            document.getElementById('qsm_id_fk').value = qsm_id;            $("#myModal_emps .close").click();        }    </script>    <script>        function check_ezn(emp_id) {            if (emp_id && !isNaN(emp_id)) {                $.ajax({                    type: 'post',                    url: "<?php echo base_url();?>human_resources/employee_forms/all_ozonat/Ezn_order/check_ezn",                    data: {emp_id: emp_id},                    success: function (msg) {                        if (msg == 0) {                            console.warn("msg :: " + msg);                            swal({                                title: "تنبيه هام !  ",                                text: "يوجد لديك إذن سابق لم يتم إعتماده ",                                type: 'warning',                                confirmButtonText: "تم",                            }, function (is_confirm) {                                var update = $('#update').val();                                console.log(update);                                if (update == 0) {                                   // $('#add_ezn').attr('disabled', 'disabled');                                } else if (update == 1) {                                    $('#add_ezn').removeAttr('disabled');                                    $('#but').attr('disabled', 'disabled');                                }                            });                        } else if (msg == 1) {                            console.warn("msg :: " + msg);                            $('#add_ezn').removeAttr('disabled');                        } else {                            console.warn("msg :: " + msg);                            var obj = JSON.parse(msg);                            $('#vacation_start').val(obj.agaza_from_date_m);                            $('#vacation_end').val(obj.agaza_to_date_m);                            $('#add_ezn').removeAttr('disabled');                        }                    }                });            }        }    </script>    <script>        function get_emp_sidebar() {            var emp_id = $('#emp_id_fk').val();            if (emp_id && !isNaN(emp_id)) {                $.ajax({                    type: 'post',                    url: "<?php echo base_url();?>human_resources/employee_forms/all_agazat/all_orders/Vacation/get_emp_data",                    data: {emp_id: emp_id},                    success: function (d) {                        $('#sidebar_emp').html(d);                    }                });            }        }        function get_available() {            var emp_id = $('#emp_id_fk').val();            var no3_ezn = $('#no3_ezn').val();            if (emp_id && !isNaN(emp_id) && no3_ezn == 1) {                $.ajax({                    type: 'post',                    url: "<?php echo base_url();?>human_resources/employee_forms/all_ozonat/Ezn_order/get_available",                    data: {emp_id: emp_id},                    success: function (d) {                        var data = JSON.parse(d);                        $('#remain_hours').val(data.remain_hours);                        $('#remain_num').val(data.remain_num);                                                if(data.remain_num <= 0  ){                          //  alert('sss');                            $('#add_ezn').attr('disabled', 'disabled');                        }                                            }                });            }        }        window.addEventListener('load', function () {            console.log('document ready');            get_emp_sidebar();            console.log('document ready');            check_ezn($('#emp_id_fk').val());            get_available();        });    </script>    <?php    if (!isset($_POST['from_profile'])) { ?>        <script>            window.addEventListener('load', function () {                /*                                $('#from_time').mdtimepicker(); //Initializes the time picker                */            });        </script>        <script>            window.addEventListener('load', function () {                /*                                $('#to_time').mdtimepicker(); //Initializes the time picker                */            });        </script>    <?php } ?>    <script>        function get_time() {            var start = $('#from_time').val();            var end = $('#to_time').val();            //  alert(start);            //  return;            if (start != '' && end != '') {                s = start.split(':');                e = end.split(':');                var ss = s[s.length - 1].split(' ');                var ee = e[e.length - 1].split(' ');                if (ss[ss.length - 1] === 'PM') {                    if (parseInt(s[0]) === 12) {                    } else {                        s[0] = parseInt(s[0]) + 12;                    }                }                if (ee[ee.length - 1] === 'PM') {                    if (parseInt(e[0]) === 12) {                    } else {                        e[0] = parseInt(e[0]) + 12;                    }                }                min = parseInt(e[1]) - parseInt(s[1]);                console.log('min :' + min + " e[1] :" + e[1] + " s[1] :" + s[1]);                hour_carry = 0;                if (min < 0) {                    min += 60;                    hour_carry += 1;                }                console.log('min :' + min);                hour = e[0] - s[0] - hour_carry;                diff = hour + "." + min;                console.log('min :' + min + " hours :" + hour + " diff :" + diff);                diff_munites = (diff* 60);                $('#num_hours').val(diff_munites);                if (diff_munites <= 0) {                    $('#num_hours').val(0);                    document.getElementById("add_ezn").disabled = true;                    document.getElementById("span_id").style.display = 'block';                    document.getElementById("span_id").innerText = 'من فضلك ادخل فترة زمنية صحيحة';                } else {                    document.getElementById("add_ezn").disabled = false;                    document.getElementById("span_id").style.display = 'none';                    check_setting_time($('#emp_id_fk').val());                }            }        }    </script>    <!-- yaraa22-9 -->    <!-- yarrraa script-->    <script>        var notify_no3_ezn;        function check_setting(emp_id) {            var no3_ezn = $('#no3_ezn').val();            var ezn_date = $('#ezn_date').val();            if (emp_id && !isNaN(emp_id) && no3_ezn == 1) {                $.ajax({                    type: 'post',                    url: "<?php echo base_url();?>human_resources/employee_forms/all_ozonat/Ezn_order/check_setting",                    data: {emp_id: emp_id, no3_ezn: no3_ezn, ezn_date: ezn_date},                    beforeSend: function () {                        notify_no3_ezn = $.notify('<strong>جاري</strong> التحقق من عدد الاذونات...', {                            placement: {                                from: "top",                                align: "center"                            },                            offset: 20,                            spacing: 10,                            delay: 1000 * 15,                            z_index: 1031,                            allow_dismiss: false,                            showProgressbar: true                        });                    },                    success: function (msg) {                        if (msg >= 1) {                            console.warn("msg :: " + msg);                            notify_no3_ezn.update({                                'type': 'danger',                                'message': '<strong> تم تحقق </strong> لا يمكنك عمل طلب اذن نظرا لتجاوز عدد الاذونات عن الحد الاقص ',                                'progress': 80                            });                            // notify_no3_ezn.close();                            swal({                                title: " لا يمكنك عمل طلب اذن ",                                text: "  نظرا لتجاوز عدد الاذونات عن الحد الاقصي",                                type: 'warning',                                confirmButtonText: "تم",                            }, function (is_confirm) {                                $('#add_ezn').attr('disabled', 'disabled');                            });                        } else {                            console.warn("msg :: " + msg);                            notify_no3_ezn.update({                                'type': 'success',                                'message': '<strong> تم تحقق </strong>  من عدد الاذونات يمكنك عمل اذن ',                                'progress': 80                            });                            $('#add_ezn').removeAttr('disabled');                        }                    }                });            }        }    </script>    <!-- check_setting_time -->    <script>        var notify_time;        function check_setting_time(emp_id) {            var from_hour = $('#from_time').val();            var to_hour = $('#to_time').val();            var no3_ezn = $('#no3_ezn').val();            var ezn_date = $('#ezn_date').val();            if (emp_id && !isNaN(emp_id) && to_hour != '' && from_hour != '' && no3_ezn == 1) {                var num_hour = $('#num_hours').val();               // alert(num_hour);                $.ajax({                    type: 'post',                    url: "<?php echo base_url();?>human_resources/employee_forms/all_ozonat/Ezn_order/check_setting_time",                    data: {emp_id: emp_id, num_hours: num_hour, ezn_date: ezn_date},                    beforeSend: function () {                        notify_time = $.notify('<strong>جاري</strong> التحقق من  مدة الاذن...', {                            placement: {                                from: "top",                                align: "center"                            },                            offset: 20,                            spacing: 10,                            delay: 1000 * 15,                            z_index: 1031,                            allow_dismiss: false,                            showProgressbar: true                        });                    },                    success: function (msg) {                        if (parseInt(msg) >= 1) {                            console.warn("msg :: " + msg);                            notify_time.update({                                'type': 'danger',                                'message': '<strong> تم تحقق </strong> لا يمكنك عمل طلب اذن نظرا لتجاوز مدة الاذن عن الحد الاقصي للمدة المتاحه ',                                'progress': 80                            });                            swal({                                title: " لا يمكنك عمل طلب اذن ",                                text: "    نظرا لتجاوز مدة الاذن عن الحد الاقصي للمدة المتاحه ",                                type: 'warning',                                confirmButtonText: "تم",                            }, function (is_confirm) {                                $('#add_ezn').attr('disabled', 'disabled');                            });                        } else {                            notify_time.update({                                'type': 'success',                                'message': '<strong> تم تحقق </strong>  من مدة الاذن يمكنك عمل اذن ',                                'progress': 80                            });                            $('#add_ezn').removeAttr('disabled');                        }                    }                });            }        }    </script>    <!-- yaraa22-9 -->