ding-4">            <label class="label"> كود البنك الجديد </label>            <input type="text" class="form-control banks " name="bank_code"                   id="bank_code1"                   value="<?php if (isset($banks) && $banks != null) {                       foreach ($banks as $bank) {                           if ($bank->id == $new_bank_id_fk) {                               echo $bank->bank_code;                           } ?><?php }                   } ?>" readonly/>        </div>        <div class="form-group col-md-4 col-sm-4 padding-4">            <label class="label"> رقم الحساب الجديد<span                        style="color:red;"> (رقم الحساب لابد أن يكون 24  رقم)</span></label>            <input type="text" class="form-control  banks" maxlength="24" minlength="24"                   class="form-control bottom-input" name="bank_account_num" id="bank_account_num"                   data-validation="required"                   onfocusout="checkLength($(this).val());"                   value="<?= $new_bank_account_num ?>"/>        </div>        <div class="form-group col-md-2 col-sm-4 padding-4">            <label class="label"> صوره الحساب الجديد</label>            <input type="file" id="userfile" style="width: 85%;float: right"                   name="userfile" class="form-control banks"            >            <?php if (isset($oneData) && (!empty($oneData)) && $oneData->new_bank_image != '') {                $img_url = "uploads/human_resources/emp_banks/" . $oneData->new_bank_image;                ?>                <button class="btn btn-success btn-sm" data-toggle="modal" type="button" style="float: left"                        data-target="#modal-img" onclick="$('#image_view').attr('src','<?= base_url() . $img_url ?>')">                    <i class=" fa fa-eye"></i>                </button>            <?php } ?>        </div>    </div>    <?php if (isset($oneData) && (!empty($oneData))) {        if (isset($from