                    seleced = "";                        if (to_bank_account_num && (to_bank_account_num === obj.accountes[i].account_num)) {                            seleced = "selected";                        }                        var option = '<option value="' + obj.accountes[i].account_num + '" ' + seleced + '> ' + obj.accountes[i].account_name + '</option>';                        options += option;                    }                    $('#to_bank_account_num').html(options);                }            });        }    }    </script><script>    function print_ta3gel(row_id) {        var request = $.ajax({            // print_resrv -- print_contract            url: "<?=base_url() . 'human_resources/employee_forms/solaf/Solaf/get_solfa_print_ta3gel'?>",            type: "POST",            data: {                rkm: row_id            },        });        request.done(function (msg) {            var WinPrint = window.open('', '', 'width=800,height=700,toolbar=0,scrollbars=0,status=0');            WinPrint.document.write(msg);            WinPrint.document.close();            WinPrint.focus();            /*  WinPrint.print();             WinPrint.close();*/        });        request.fail(function (jqXHR, textStatus) {            console.log("Request failed: " + textStatus);        });    }</script>