<?php   if(isset($data_table) && !empty($data_table) && $data_table!=null){?>  <br>    <table id="example" class="table table-striped table-bordered dt-responsive nowrap" cellspacing="0" width="100%">        <thead>        <tr>            <th class="text-center">م</th>            <th class="text-center">كود الموظف</th>            <th class="text-center">إسم الموظف </th>            <th class="text-center">رقم الهوية </th>            <th class="text-center">الجنسية </th>            <th class="text-center">المسمي الوظيفي </th>            <th class="text-center">الإدارة</th>            <th class="text-center">القسم</th>            <th class="text-center">إجمالى </th>            <th class="text-center">الإجراء </th>        </tr>        </thead>        <tbody class="text-center" id="id_example">        <?php        $total =0;        $x=1; foreach ($data_table as $row){            $total +=$_POST['due_to_member'];?>            <tr  id="<?=$x?>">                <td class="text-center"><?=$x?></td>                <td class="text-center"><?=$row->emp_code?> </td>                <td class="text-center"><?=$row->employee?>                    <input type="hidden" name="name[<?=$row->emp_code?>]" value="<?=$row->employee?>">                </td>                <td class="text-center"><?=$row->card_num?>                    <input type="hidden" name="card_num[<?=$row->emp_code?>]" value="<?=$row->card_num?>">                    <input type="hidden" name="mosma_wazefy_n[<?=$row->emp_code?>]" value="<?=$row->mosma_wazefy_n?>">                    <input type="hidden" name="edara_n[<?=$row->emp_code?>]" value="<?=$row->edara_n?>">                    <input type="hidden" name="qsm_n[<?=$row->emp_code?>]" value="<?=$row->qsm_n?>">                </td>                <td class="text-center"><?=$row->nationality?> </td>                <td class="text-center"><?=$row->mosma_wazefy_n?> </td>                <td class="text-center"><?php   echo$row->edara_n; ?></td>                <td class="text-center"><?php   echo$row->qsm_n; ?></td>                <td class="text-center"><?=$_POST['due_to_member']?>                    <input type="hidden" name="value[<?=$row->emp_code?>]" value=" <?=$_POST['due_to_member']?>">                </td>                <td>                    <a  data-num="<?=$_POST['due_to_member']?>"  data-rkm="<?=$x?>" data-id="<?=$row->id?>" onclick="RemoveRow($(this));">                        <i class="fa fa-trash"> </i></a>                </td>            </tr>        <?php $x++; }?>        <tr>            <td colspan="8"> <input type="hidden" name="total_value" id="total_value"  value="<?=$total?>" /> الإجمالى</td>            <td id="total_td"><?=$total?></td>        </tr>        </tbody>        </table><?php }?><script>    function RemoveRow(obj) {        var rowCount = document.getElementById("id_example").rows.length;        if(rowCount ==1 || rowCount ==0){            $('#submit_but').prop('disabled', true);        } else {            $('#submit_but').prop('disabled', false);        }        // alert(rowCount);        var total =$('#total_value').val();        var num =obj.attr('data-num');        //alert(total);        //alert(num);        //var emp_id =obj.attr('data-id');        //$('#'+emp_id).show();        var id =obj.attr('data-rkm');        $('#'+id).remove();        $('#total_value').val((total-num));        $('#total_td').text((total-num));    }</script>