l_sarf_td').text();        var num =obj.attr('data-num');        var emp_id =obj.attr('data-id');        $('#'+emp_id).show();        //$('#choose' + emp_id).attr('class','checkbox_emp');        var id =obj.attr('data-rkm');        $('#'+id).remove();        $('#total_sarf_td').text((total-num));        //alert(total);    }</script>