<!-- yara --><?phpif (isset($morfqat) && !empty($morfqat)) {    $x = 1;    $image = array('gif', 'Gif', 'ico', 'ICO', 'jpg', 'JPG', 'jpeg', 'JPEG', 'BNG', 'png', 'PNG', 'bmp', 'BMP');    $file = array('pdf', 'PDF', 'xls', 'xlsx', ',doc', 'docx', 'txt');    ?>    <table id="example88" class="table example table-bordered table-striped table-hover">        <thead>        <tr class="greentd">            <th>م</th>            <th>نوع الملف</th>            <th>اسم الملف</th>            <th> الملف</th>            <th>الحجم</th>            <th>التاريخ</th>            <th>بواسطة</th>            <th>الاجراء</th>        </tr>        </thead>        <tbody>        <?php        foreach ($morfqat as $morfaq) {            $ext = pathinfo($morfaq->file, PATHINFO_EXTENSION);            //  $Destination = 'uploads/archive/deals/1bec9894697603bd9a45630d73230be5.jpg';            $Destination = 'uploads/human_resources/tato3/morfqat/' . $morfaq->file;            if (file_exists($Destination)) {                $bytes = filesize($Destination);            } else {                $bytes = '';            }            $size = '';            if ($bytes >= 1073741824) {                $size = number_format($bytes / 1073741824, 2) . ' GB';            } elseif ($bytes >= 1048576) {                $size = number_format($bytes / 1048576, 2) . ' MB';            } elseif ($bytes >= 1024) {                $size = number_format($bytes / 1024, 2) . ' KB';            } elseif ($bytes > 1) {                $size = $bytes . ' bytes';            } elseif ($bytes == 1) {                $size = $bytes . ' byte';            } else {                $size = '0 bytes';            }            ?>            <tr>                <td><?= $x++ ?></td>                <td>                    <?php                    if (in_array($ext, $image)) {                        ?>                        <i class="fa fa-picture-o" aria-hidden="true" style="color: #d93bff;"></i>                    <?php } elseif (in_array($ext, $file)) { ?>                        <i class="fa fa-file-pdf-o" aria-hidden="true" style="color: red;"></i>                    <?php }                    ?>                </td>                <td>                    <?php                    if (!empty($morfaq->title)) {                        echo $morfaq->title;                    } else {                        echo 'لا يوجد ';                    }                    ?>                </td>                <td>                    <?php                    if (in_array($ext, $image)) {                        ?>                        <a onclick="get_image('<?= $morfaq->file ?>')" data-toggle="modal" data-target="#myModal-view">                            <i class="fa fa-eye" title=" قراءة"></i> </a>                        <?php                    } elseif (in_array($ext, $file)) {                        ?>                        <a onclick="get_pdf('<?= $morfaq->file ?>')" data-toggle="modal" data-target="#myModal-pdf">                            <i class="fa fa-eye" title=" قراءة"></i> </a>                        <?php                    }                    ?>                </td>                <td>                    <?= $size ?>                </td>                <td>                    <?php                    if (!empty($morfaq->date_ar)) {                        echo $morfaq->date_ar;                    } else {                        echo 'غير محدد  ';                    }                    ?>                </td>                <td>                    <?php                    if (!empty($morfaq->publisher_name)) {                        echo $morfaq->publisher_name;                    } else {                        echo 'غير محدد  ';                    }                    ?>                </td>                <td>                    <!-- <a href="<?= base_url() . $folder . '/' . $morfaq->file ?>" download>  <i class="fa fa-download fa-2x" aria-hidden="true" ></i> </a> -->                    <i class="fa fa-trash" style="cursor: pointer"                       onclick="delete_t_morfaq(<?= $morfaq->id ?>,<?= $morfaq->tat_id_fk ?>)"></i>                </td>            </tr>            <?php        }        ?>        </tbody>    </table>    <?php}?><script>    function delete_t_morfaq(row_id, tat_id_fk) {        swal({                title: "هل أنت متأكد من الحذف ؟",                text: "",                type: "warning",                showCancelButton: true,                confirmButtonClass: "btn-danger",                confirmButtonText: "نعم",                cancelButtonText: "لا",                closeOnConfirm: true            },            function () {                $.ajax({                    type: 'post',                    url: '<?php echo base_url()?>human_resources/tataw3/Tataw3_c/delete_t_morfaq',                    data: {tat_id_fk: tat_id_fk, row_id: row_id},                    dataType: 'html',                    cache: false,                    beforeSend: function () {                        $('#myDiv_morfq').html(                            '<div class=\'loader-img\'><div class=\'bar1\'></div><div class=\'bar2\'></div><div class=\'bar3\'></div><div class=\'bar4\'></div><div class=\'bar5\'></div><div class=\'bar6\'></div></div>'                        );                    },                    success: function (html) {                        $('#myDiv_morfq').html(html);                        swal({                            title: "تم الحذف بنجاح",                            confirmButtonText: "تم"                        });                    }                });            });    }</script><script>    $('#example88').DataTable({        dom: 'Bfrtip',        buttons: [            'pageLength',            'copy',            'csv',            'excelHtml5',            {                extend: "pdfHtml5",                orientation: 'landscape'            },            {                extend: 'print',                exportOptions: {columns: ':visible'},                orientation: 'landscape'            },            'colvis'        ],        colReorder: true    });</script>