ipt src="<?php echo base_url(); ?>asisst/admin_asset/js/tables/dataTables.buttons.min.js"></script><script>    $('#add_emp').DataTable({        dom: 'Bfrtip',        buttons: [            'pageLength'],        colReorder: true    });</script><script>    function check_all(elem, table_id) {        var oTable = $('.' + table_id).dataTable();        var rowcollection = oTable.$(".checkbox", {"page": "all"});        rowcollection.each(function (index, obj) {                obj.checked = elem.checked;            }        );    }    function get_checed_box() {        var members = [];        var oTable = $('.add_emp').dataTable();        var rowcollection = oTable.$(".checkbox:checked", {"page": "all"});        rowcollection.each(function (index, elem) {            members.push($(elem).data('id'));        });        // $('#emps_id').val(members);    }</script><script></script>