tables/dataTables.buttons.min.js"></script><script>    $('#tahwel').DataTable({        dom: 'Bfrtip',        buttons: [            'pageLength',        ],        colReorder: true    });</script><script>    function check_all(elem, table_id) {        var oTable = $('.' + table_id).dataTable();        var rowcollection = oTable.$(".checkbox", {"page": "all"});        rowcollection.each(function (index, obj) {                obj.checked = elem.checked;                console.log(obj.dataset.type);                var id = obj.name;                var type = 1;                var name = obj.value;                if (obj.checked) {                    Gettahwel(id, name, type);                } else {                    $("#" + id).remove();                    $("#type" + id).remove();                    $("#to_user_fk" + id).remove();                    $("#to_user_fk_name" + id).remove();                }            }        );    }</script><script>    function Gettahwel(id, name, type) {        $('#text111').hide();        $("ol").append("<li name=f id='" + id + "'>" + name + "</li>");        $('#to_user_n').append("<input type='hidden'  id='to_user_fk" + id + "' name='to_user_fk[]' value='" + id + "'/>" +            "<input type='hidden'  data-validation='required' id='to_user_fk_name" + id + "'  name='to_user_fk_name[]' value='" + name + "'/><input type='hidden'  data-validation='required' id='type" + id + "' name='type[]' value='" + type + "'/>");    }</script>