div class="modal" id="myModal_attache" tabindex="-1" role="dialog" aria-labelledby="myModalLabel"     data-wow-duration="0.5s">    <div class="modal-dialog" role="document" style="width: 90%;">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 class="modal-title">                    أضافة مرفقات                </h4>            </div>            <div class="modal-body">                <div id="result_attach"></div>            </div>            <div class="modal-footer" style="display: inline-block; width: 100%;">                <button type="button" class="btn btn-danger btn-labeled" data-dismiss="modal"><span class="btn-label"><i                                class="fa fa-remove"></i></span>إغلاق                </button>            </div>        </div>    </div></div><!-- myModal_attache --><div class="modal fade" id="detailsModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel">    <div class="modal-dialog" role="document" style="width: 85%">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span>                </button>                <h4 class="modal-title" id="myModalLabel">التفاصيل</h4>            </div>            <div class="modal-body col-sm-12" id="detail_div">            </div>            <div class="modal-footer" style="display: inline-block;width: 100%">                <button type="button" class="btn btn-danger" style="float: left;" data-dismiss="modal">إغلاق</button>            </div>        </div>    </div></div><!-- myModal_attache --><script>    function get_attach_data(id) {        var dataString = "id=" + id;        $.ajax({            type: 'post',            url: '<?php echo base_url() ?>family_controllers/talbat/Talb_main_request/add_picture',            data: dataString,            dataType: 'html',            cache: false,            beforeSend: function () {                $('#result_attach').html('<div class=\'loader-img\'><div class=\'bar1\'></div><div class=\'bar2\'></div><div class=\'bar3\'></div><div class=\'bar4\'></div><div class=\'bar5\'></div><div class=\'bar6\'></div></div>');            },            success: function (html) {                $("#result_attach").html(html);            }        });    }</script><script>    function GetTransferPage(value) {        if (value != 0 && value != '') {            var dataString = 'id=' + value;            $.ajax({                type: 'post',                url: '<?php echo base_url() ?>family_controllers/talbat/Talb_main_request/GetTransferPage',                data: dataString,                dataType: 'html',                cache: false,                beforeSend: function () {                    $('#detail_div').html('<div class=\'loader-img\'><div class=\'bar1\'></div><div class=\'bar2\'></div><div class=\'bar3\'></div><div class=\'bar4\'></div><div class=\'bar5\'></div><div class=\'bar6\'></div></div>');                },                success: function (html) {                    $("#detail_div").html(html);                }            });        }    }</script><script>    function print_contrat(id) {        var request = $.ajax({            // print_resrv -- print_contract            url: "<?=base_url() . 'family_controllers/talbat/Talb_main/print/'?>" + id,            type: "get",        });        request.done(function (msg) {            var WinPrint = window.open('', '', 'width=800,height=700,toolbar=0,scrollbars=0,status=0');            WinPrint.document.write(msg);            WinPrint.focus();        });        request.fail(function (jqXHR, textStatus) {            console.log("Request failed: " + textStatus);        });    }</script><script>    function delete_morfq(id, row) {        swal({                title: "هل انت متاكد من الحذف?",                type: "warning",                showCancelButton: true,                confirmButtonClass: "btn-danger",                confirmButtonText: "نعم!",                cancelButtonText: "لا!",                closeOnConfirm: false,                closeOnCancel: false            },            function (isConfirm) {                if (isConfirm) {                    $.ajax({                        type: 'post',                        url: '<?php echo base_url() ?>family_controllers/talbat/Talb_main_request/delete_morfq',                        data: {id: id},                        dataType: 'html',                        cache: false,                        success: function (html) {                            //   alert(html);                            // $('#Modal_esdar').modal('hide');                            swal({                                    title: "تم الحذف بنجاح!",                                }                            );                            console.log(row);                            get_details(row);                        }                    });                } else {                    swal("تم الالغاء", "", "error");                }            });    }</script><script>    function get_details(id) {        // $('#pop_rkm').text(rkm);        $.ajax({            type: 'post',            data: {id: id},            url: "<?php echo base_url();?>family_controllers/talbat/Talb_main_request/load",            beforeSend: function () {                $('#morfaq_result').html('<div class=\'loader-img\'><div class=\'bar1\'></div><div class=\'bar2\'></div><div class=\'bar3\'></div><div class=\'bar4\'></div><div class=\'bar5\'></div><div class=\'bar6\'></div></div>');            },            success: function (d) {                $('#morfaq_result').html(d);            }        });    }</script>