<!-- Profile Image --><?php if ((isset($new_emp)) && (!empty($new_emp))) { ?>    <ul class="products-list product-list-in-card pl-2 pr-2">        <?php foreach ($new_emp as $one_emp) {            if ((!empty($one_emp['emp_img'])) && (file_exists('uploads/human_resources/emp_photo/thumbs/' . $one_emp['emp_img']))) {                $img_url = 'uploads/human_resources/emp_photo/thumbs/' . $one_emp['emp_img'];            } else {                $img_url = 'asisst/admin_asset/img/avatar5.png';            } ?>            <li class="item">                <div class="product-img">                    <img src="<?= base_url() . $img_url ?>" alt=" " class="img-size-50 img-circle">                </div>                <div class="product-info">                    <a href="#" class="product-title">                        <?= $one_emp['emp_name'] ?>                        <span class="badge1 badge-success float-right">إرسال رسالة ترحيب </span></a>                    <span class="product-description">                       <?= $one_emp['mosma_wazefy_n'] ?>                      </span>                    <p class="pargraph job"><i class="fa fa-phone" aria-hidden="true"></i> <?= $one_emp['phone'] ?>                        &nbsp;&nbsp; <i class="fa fa-mobile" aria-hidden="true"></i> <?= $one_emp['another_phone'] ?>                    </p>                </div>            </li>        <?php } ?>    </ul>    <?php} else { ?>    <?php} ?>