//web.whatsapp.com/send?phone=0201092886023&text="+full_message+"&source&data";                if (window.showModalDialog){                    window.showModalDialog(link,"popup","dialogWidth:600px;dialogHeight:600px");                }else{                    window.open(link,"name","height=600,width=600,toolbar=no,directories=no,status=no,menubar=no,scrollbars=no,resizable=no,modal=yes");                }                $('#send_data_whats').modal('hide');                $('#message_whats').val('');                $('#message_id').val('');                $('#jwal').val('');            }        }    </script>    <script>        function get_member_send_whats(valu,jwal)        {            $('#message_id').val(valu);            $('#jwal').val(jwal);        }    </script><script>    function load_page(row_id) {        $.ajax({            type: 'post',            url: "<?php echo base_url();?>md/gam3ia_contact/Contact/load_message",            data: {row_id:row_id},            beforeSend: function() {                $('#result').html(                    '<div class=\'loader-img\'><div class=\'bar1\'></div><div class=\'bar2\'></div><div class=\'bar3\'></div><div class=\'bar4\'></div><div class=\'bar5\'></div><div class=\'bar6\'></div></div>'                );            },            success: function (d) {                $('#result').html(d);            }        });    }</script>    <script>//        22-3-om        function update_egraa() {            var row_id = $('#row_id').val();            var egraa_id_fk = $('#egraa_id_fk').val();            var egraa_note = $('#egraa_note').val();            console.log(egraa_id_fk) ;         //   return;            if (egraa_id_fk !=''){                $.ajax({                    type: 'post',                    url: "<?php echo base_url();?>md/gam3ia_contact/Contact/update_egraa",                    data: {row_id: row_id, egraa_id_fk: egraa_id_fk, egraa_note: egraa_note},                    beforeSend: function() {                        $('#main_content').html(                            '<div class=\'loader-img\'><div class=\'bar1\'></div><div class=\'bar2\'></div><div class=\'bar3\'></div><div class=\'bar4\'></div><div class=\'bar5\'></div><div class=\'bar6\'></div></div>'                        );                    },                    success: function (d) {                        $('#row_id').val('');                        $('#egraa_id_fk').val('');                        swal({                            title:" تمت العملية بنجاح ",                        });                        $('#egraaModal').modal('hide');                        $('#main_content').html(d);                    }                });            }  else {                swal({                    title:"من فضلك تأكد من الحقول الناقضه !",                    type:"warning"                });            }        }    </script>