ctived = 'class="active"';                            $color = 'red';                        }                        ?>                        <li <?php echo $actived; ?> >                            <a href="<?php echo base_url() . $row->page_link ?>"><?php echo $row->page_title ?></a>                        </li>                        <?php $x++; endforeach; ?>                </ul>            </div>        </div>    </section><?php } ?><div class="content" id="Main-content">    <div class="row">        <div id="preloading"></div><!--loading will be showen here-->        <div class="box-body">            <div id="messagebox">                <?php if (isset($_SESSION['message'])): ?>                    <?php echo $_SESSION["message"];                    unset($_SESSION["message"]) ?>                <?php endif; ?>            </div>        </div>